mapper.select:
select u.user_id, u.user_name, u.real_name, u.pass_word, u.gender, u.phone, u.status, u.create_time, u.last_login_time, r.role_id, r.role_name from t_admin_user u join t_admin_user_role ur on u.user_id=ur.user_id join t_admin_role r on ur.role_id=r.role_id
and u.user_name=#{userName,jdbcType=VARCHAR}
and u.real_name=#{realName,jdbcType=VARCHAR}
and u.phone=#{phone,jdbcType=VARCHAR}
and u.status=#{status,jdbcType=VARCHAR}
and u.create_time >= #{startCreateTime,jdbcType=TIMESTAMP}
and u.create_time < date_add(#{endCreateTime,jdbcType=TIMESTAMP}, INTERVAL 1 DAY)
